library(ape)

# Simulated sequences

syn_fas <- read.FASTA("./data/syn.fas") |> as.character() 
syn_fas <- lapply(syn_fas, paste, collapse="") 
syn_fas <- sapply(syn_fas, toupper)
names(syn_fas) <- gsub("B/", "", names(syn_fas))


# Coding sequences of the first exon of beta-globin gene

human    <- toupper("atggtgcacctgactcctgaggagaagtctgccgttactgccctgtggggcaaggtgaacgtggatgaagttggtggtgaggccctgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/455025)
chimp    <- toupper("atggtgcacctgactcctgaggagaagtctgccgttactgccctgtggggcaaggtgaacgtggatgaagttggtggtgaggccctgggcaggttggtatcaagg") #(https://www.ncbi.nlm.nih.gov/nuccore/X02345)
mouse    <- toupper("atggtgcacctgactgatgctgagaagtctgctgtctcttgcctgtgggcaaaggtgaaccccgatgaagttggtggtgaggccctgggcagg") #(https://www.ncbi.nlm.nih.gov/nuccore/V00722)
rat      <- toupper("atggtgcacctaactgatgctgagaaggctactgttagtggcctgtggggaaaggtgaaccctgataatgttggcgctgaggccctgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/X06701)
gallus   <- toupper("atggtgcactggactgctgaggagaagcagctcatcaccggcctctggggcaaggtcaatgtggccgaatgtggggccgaagccctggccag") #(https://www.ncbi.nlm.nih.gov/nuccore/V00409)
gorilla  <- toupper("atggtgcacctgactcctgaggagaagtctgccgttactgccctgtggggcaaggtgaacgtggatgaagttggtggtgaggccctgggcagg") #(https://www.ncbi.nlm.nih.gov/nuccore/X61109)
rabbit   <- toupper("atggtgcatctgtccagtgaggagaagtctgcggtcactgccctgtggggcaaggtgaatgtggaagaagttggtggtgaggccctgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/V00882)
opossum  <- toupper("atggtgcacttgacttctgaggagaagaactgcatcactaccatctggtctaaggtgcaggttgaccagactggtggtgaggcccttggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/J03643)
lemur    <- toupper("atgactttgctgagtgctgaggagaatgctcatgtcacctctctgtggggcaaggtggatgtagagaaagttggtggcgaggccttgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/M15734)
goat     <- toupper("atgctgactgctgaggagaaggctgccgtcaccggcttctggggcaaggtgaaagtggatgaagttggtgctgaggccctgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/M15387)
bovine   <- toupper("atgctgactgctgaggagaaggctgccgtcaccgccttttggggcaaggtgaaagtggatgaagttggtggtgaggccctgggcag") #(https://www.ncbi.nlm.nih.gov/nuccore/X00376)

beta_seq <- cbind(human, chimp, mouse, rat, gallus, gorilla, rabbit, opossum, lemur, goat, bovine)
animals <- dimnames(beta_seq)[[2]]
beta_seq <- c(beta_seq)
names(beta_seq) <- animals

